import { useState } from "react" 
import validacion from "./validacion";
//! gi
//https://mir-s3-cdn-cf.behance.net/project_modules/disp/c50a4a55883023.5996f8afa3f5c.gif
const Form = ({login})=>{

    const [userData, setUserData] = useState({
        username: "",
        password: "" , 
        
    });
    const[errors, setErrors]= useState({
        username: "",
        password: "" , 
    })

     const handleInputChange= (event) =>{
        const property = event.target.name;
        const value = event.target.value;

        setUserData({...userData, [property]: value})
        validacion({...userData, [property]: value}, errors, setErrors)
        
    }
     const submitHandler = (event)=>{
        event.preventDefault();
        login(userData)
     };
     
    return(
 <form onSubmit={submitHandler}>
    <div>
    <label htmlFor="username">USername:</label>
        <input type="email" name="username"
               value={userData.username} onChange={handleInputChange}/>  
    </div>

    <div>
    <label htmlFor="password">Password:</label>
        <input type="password" name="password" 
               value={userData.password} onChange={handleInputChange}/>
    </div>
        <button type="submit">LOGIN</button>
</form>
)}

export default Form
