import Cards from "./components/Cards/Cards";
import Nav from "./components/Nav/Nav.jsx";
import { useEffect, useState } from "react";
import style from "./App.module.css";
import { Navigate, Route, Routes, useLocation, useNavigate} from "react-router-dom";
import About from "./components/About/About"
import Detail from "./components/Detail/Detail.jsx"
import Form from "./components/Form/Form";


 export function App() {
  const [characters, setCharacters] = useState([]);
  const {pathname} = useLocation();
  const [access, setAccess] = useState(false)
  const navigate = useNavigate();

  useEffect
  const password = "Enma2707."
  const username= "enmareco.co@gmail.com"

  const onSearch = (id) => {
    const URL_BASE = "https://be-a-rym.up.railway.app/api";
    const KEY = "277f6ade46a8.202ec27b1169af7ce0ad";
    if(characters.find((char) => char.id === id)){
      return alert("Personaje seleccionado");
    }

    fetch(`${URL_BASE}/character/${id}?key=${KEY}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.name){
          setCharacters((oldChars) => [...oldChars, data]);
        } else {
          alert("Algo salió mal");
        }
      });
  };
  const onClose = (id) => {
    
   setCharacters(characters.filter((char) => char.id !== id));
  };

    const login = (userData)=>{
      if(userData == username && userData == password){
          setAccess(true);
          navigate("/home");
      }
    }

  return (
  <div>
    {pathname !== "/" && <Nav onSearch={onSearch} className={style.nav}/>} 
    <Routes>
      <Route path="/" element={<Form/>}/>
      <Route
        path="/home"
        element={<Cards characters={characters} onClose={onClose} />}
      />

      <Route 
        path="/about" element={<About/>}/>
      <Route
        path="/detail/:detailId" element={<Detail/>}/>
    </Routes>
  </div> 
  )
}

export default App;
